import {useMemo} from 'react';

export function useCombinedRefs<T>(
  ...refs: ((node: T) => void)[]
): (node: T) => void {
  return useMemo(
    () => (node: T) => {
      refs.forEach((ref) => ref(node));
    },
    // eslint-disable-next-line react-hooks/exhaustive-deps
    refs
  );
}
// https://github.com/facebook/react/blob/master/packages/shared/ExecutionEnvironment.js
export const canUseDOM =
  typeof window !== 'undefined' &&
  typeof window.document !== 'undefined' &&
  typeof window.document.createElement !== 'undefined';
export function isWindow(element: Object): element is typeof window {
  const elementString = Object.prototype.toString.call(element);
  return (
    elementString === '[object Window]' ||
    // In Electron context the Window object serializes to [object global]
    elementString === '[object global]'
  );
}
export function isNode(node: Object): node is Node {
  return 'nodeType' in node;
}
import {isWindow} from '../type-guards/isWindow';
import {isNode} from '../type-guards/isNode';

export function getWindow(target: Event['target']): typeof window {
  if (!target) {
    return window;
  }

  if (isWindow(target)) {
    return target;
  }

  if (!isNode(target)) {
    return window;
  }

  return target.ownerDocument?.defaultView ?? window;
}
import {getWindow} from '../execution-context/getWindow';

export function isDocument(node: Node): node is Document {
  const {Document} = getWindow(node);

  return node instanceof Document;
}
import {getWindow} from '../execution-context/getWindow';

import {isWindow} from './isWindow';

export function isHTMLElement(node: Node | Window): node is HTMLElement {
  if (isWindow(node)) {
    return false;
  }

  return node instanceof getWindow(node).HTMLElement;
}
import {getWindow} from '../execution-context/getWindow';

export function isSVGElement(node: Node): node is SVGElement {
  return node instanceof getWindow(node).SVGElement;
}
import {isWindow, isHTMLElement, isDocument, isNode} from '../type-guards';

export function getOwnerDocument(target: Event['target']): Document {
  if (!target) {
    return document;
  }

  if (isWindow(target)) {
    return target.document;
  }

  if (!isNode(target)) {
    return document;
  }

  if (isDocument(target)) {
    return target;
  }

  if (isHTMLElement(target)) {
    return target.ownerDocument;
  }

  return document;
}
