import React from 'react';

interface Props {
  id: string;
  value: string;
}

const hiddenStyles: React.CSSProperties = {
  display: 'none',
};

export function HiddenText({id, value}: Props) {
  return (
    <div id={id} style={hiddenStyles}>
      {value}
    </div>
  );
}
import React from 'react';

export interface Props {
  id: string;
  announcement: string;
}

// Hide element visually but keep it readable by screen readers
const visuallyHidden: React.CSSProperties = {
  position: 'fixed',
  width: 1,
  height: 1,
  margin: -1,
  border: 0,
  padding: 0,
  overflow: 'hidden',
  clip: 'rect(0 0 0 0)',
  clipPath: 'inset(100%)',
  whiteSpace: 'nowrap',
};

export function LiveRegion({id, announcement}: Props) {
  return (
    <div
      id={id}
      style={visuallyHidden}
      role="status"
      aria-live="assertive"
      aria-atomic
    >
      {announcement}
    </div>
  );
}
import {useCallback, useState} from 'react';

export function useAnnouncement() {
  const [announcement, setAnnouncement] = useState('');
  const announce = useCallback((value: string | undefined) => {
    if (value != null) {
      setAnnouncement(value);
    }
  }, []);

  return {announce, announcement} as const;
}
