import React from "react"
import {
  DndContext,
  closestCenter,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
} from "@dnd-kit/core"
import {
  SortableContext,
  verticalListSortingStrategy,
  sortableKeyboardCoordinates,
} from "@dnd-kit/sortable"
import {
  restrictToVerticalAxis,
  restrictToWindowEdges,
} from "@dnd-kit/modifiers"
import styles from "./TodoList.module.css"
import Todo from "./Todo"
/* 
const Todo = React.lazy(() =>import("./Todo")) */

function TodoList({
  todos,
  toggleTodo,
  deleteTodo,
  setEditedTodo,
  setEditID,
  editedTodo,
  editID,
  editTodo,
  handleDragEnd,
}) {
  const sensors = useSensors(
    useSensor(PointerSensor),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  )

  return (
    <div
      className={styles.todoListContainer}
      /* onClick={(e) => e.stopPropagation()} */
    >
      {!todos.length && <h2>The list is empty</h2>}
      <DndContext
        autoScroll={false}
        sensors={sensors}
        collisionDetection={closestCenter}
        onDragEnd={handleDragEnd}
        modifiers={[restrictToVerticalAxis, restrictToWindowEdges]}
      >
        <SortableContext items={todos} strategy={verticalListSortingStrategy} modifiers={[restrictToParentElement]}>
          {todos.map((todo) => (
            <Todo
              /* classname={todo.isCompleted ? 'completedTodo' : ''} */
              todo={todo}
              key={todo.id}
              toggleTodo={toggleTodo}
              deleteTodo={deleteTodo}
              editTodo={editTodo}
              setEditedTodo={setEditedTodo}
              setEditID={setEditID}
              editedTodo={editedTodo}
              editID={editID}
            />
          ))}
        </SortableContext>
      </DndContext>
    </div>
  )
}

export default TodoList
