        if (speed.x > 0 || speed.y > 0) {
          clearAutoScrollInterval();

          scrollContainerRef.current = scrollContainer;
          setAutoScrollInterval(autoScroll, interval);

          scrollSpeed.current = speed;
          scrollDirection.current = direction;

          return;
