      return null;
    }

    const scaleAdjustedTransform = adjustScale
      ? transform
      : {
          ...transform,
          scaleX: 1,
          scaleY: 1,
        };
    const styles: React.CSSProperties | undefined = {
      ...baseStyles,
      width: rect.width,
      height: rect.height,
      top: rect.top,
      left: rect.left,
      transform: CSS.Transform.toString(scaleAdjustedTransform),
      transformOrigin:
        adjustScale && activatorEvent
