import React from "react"
import { MdDragIndicator, MdOutlineModeEditOutline } from "react-icons/md"
import { RiDeleteBin6Line } from "react-icons/ri/"
import { useSortable } from "@dnd-kit/sortable"

import {CSS} from "@dnd-kit/utilities"
import TodoEditInput from "./TodoEditInput"
import CompletedIcon from "../UI/СompletedIcon"
import styles from "./Todo.module.css"

function Todo({
  todo,
  toggleTodo,
  deleteTodo,
  setEditID,
  setEditedTodo,
  editedTodo,
  editID,
  editTodo
}) {

  const {attributes, listeners, setNodeRef,setActivatorNodeRef, transform, transition, restrictToParentElement } = useSortable({id: todo.id},{transition: {
    duration: 150, // milliseconds
    easing: 'cubic-bezier(0.25, 1, 0.5, 1)',
  }})

  const style = {
    transform: CSS.Transform.toString(transform),
    transition
  }

  const randomHeights = createRange(props.itemCount).map(() => {
    const heights = [110, undefined, 140, undefined, 90, undefined];
    const randomHeight = heights[Math.floor(Math.random() * heights.length)];

    return randomHeight;
  });


  return (
    <div  ref={setNodeRef} style={style} {...attributes} modifiers={[restrictToParentElement]} className={styles.todoContainer}>
      {editID === todo.id ? (
        <TodoEditInput
          editTodo={editTodo}
          setEditedTodo={setEditedTodo}
          editedTodo={editedTodo}
          setEditID={setEditID}
          editID={editID}
          todo={todo}
        />
      ) : (
        <>
          <MdDragIndicator className={styles.dragIcon} {...listeners} ref={setActivatorNodeRef}/>
          <div
            className={`${styles.todoOutlined} ${
              todo.isCompleted ? styles.completedTodo : ""
            }`}
            onClick={() => toggleTodo(todo.id)}
          >
            <div className={styles.todo}>
              {todo.isCompleted && (
                <div className={styles.completedIconContainer}>
                  <CompletedIcon />
                  <div className={styles.completedCircle}></div>
                </div>
              )}

              <div className={styles.todoText}>{todo.text}</div>

              {!todo.isCompleted && (
                <MdOutlineModeEditOutline
                  className={styles.editIcon}
                  onClick={(e) => {
                    e.stopPropagation()
                    setEditID(todo.id)
                    setEditedTodo(todo.text)
                  }}
                />
              )}
              <RiDeleteBin6Line
                className={styles.deleteIcon}
                onClick={(e) => {
                  e.stopPropagation()
                  deleteTodo(todo.id)
                }}
              />
            </div>
          </div>
        </>
      )}
    </div>
  )
}

export default Todo
